import React, { useState, useEffect } from "react";
import { getNotificationsApi, followUserApi } from "../../apis/Api"; // Import your API functions

const Notification = ({ notification, onFollowBack }) => {
  const handleFollowBack = () => {
    // Call the follow back function passed from the parent component
    onFollowBack(notification.sender._id);
  };

  return (
    <div>
      <p>
        {notification.sender.firstName} {notification.sender.lastName} followed you
        <button onClick={handleFollowBack}>Follow Back</button>
      </p>
    </div>
  );
};

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    // Fetch notifications from the backend using an API call
    const fetchNotifications = async () => {
      try {
        const response = await getNotificationsApi();
        setNotifications(response.data.notifications);
      } catch (error) {
        console.error("Error fetching notifications:", error);
      }
    };

    fetchNotifications();
  }, []);

  return (
    <div>
      <h2>Notifications</h2>
      {notifications && notifications.length === 0 ? (
        <p>No notifications</p>
      ) : (
        notifications.map((notification, index) => (
          <Notification
            key={index}
            notification={notification}
            onFollowBack={handleFollowBack}
          />
        ))
      )}
    </div>
  );
};

export default Notifications;
