import React, { useState } from 'react';
import { forgetPasswordApi } from '../apis/Api';
import { toast } from 'react-toastify';

const ForgetPassword = () => {
  const [email, setEmail] = useState('');

  const handleChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e) => {
    setEmail();

    forgetPasswordApi({ email })
      .then((res) => {
        // Assuming your backend returns a success message in res.data.message
        toast.success(res.data.message);
      })
      .catch((err) => {
        // Assuming your backend returns an error message in err.response.data.message
        toast.error(err.response.data.message);
      });

      setEmail("")
  };

  return (
    <div>
      <h2>Forget Password</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ForgetPassword;
