import React, { useState } from 'react';
import { registerApi } from '../apis/Api';
import { toast } from 'react-toastify';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Paper from '@mui/material/Paper';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import { createTheme, ThemeProvider } from '@mui/material/styles';


const Register = () => {
    const [firstName, setfirstName] = useState('')
    const [lastName, setlastName] = useState('')
    const [phoneNo, setphoneNo] = useState('')
    const [password, setPassword] = useState('')

    const changefirstName = (e) => {
        setfirstName(e.target.value)
    }
    const changelastName = (e) => {
        setlastName(e.target.value)
    }
    const changePhoneNo = (e) => {
        setphoneNo(e.target.value)
    }
    const changePassword = (e) => {
        setPassword(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        console.log(firstName,lastName,phoneNo,password)

        const data = {
            firstName : firstName,
            lastName : lastName,
            phoneNo : phoneNo,
            password : password
        }
        registerApi(data).then((res)=> {
            if(res.data.success == true){
                toast.success(res.data.message)
            }else{
                toast.error(res.data.message)
            }
        }).catch((err)=>{
            console.log(err)
            toast.error("Internal Server Error")
        })
    }
    const defaultTheme = createTheme();

  return (
    <>
    <h1 className='m-3'>Create an Account</h1>
    <form className='m-3 w-25'>
      <label>firstName</label>
      <input onChange={changefirstName} className='form-control mb-2' type="text" placeholder='Enter your firstName'></input>

      <label>lastName</label>
      <input onChange={changelastName} className='form-control mb-2' type='text' placeholder='Enter your lastName'></input>

      <label>PhoneNo</label>
      <input onChange={changePhoneNo} className='form-control mb-2' type='text' placeholder='Enter your number'></input>

      <label>Password</label>
      <input onChange={changePassword} className='form-control mb-3' type='text' placeholder='Enter your password'></input>

      <button onClick={handleSubmit} className='btn btn-danger w-100'>Submit</button>
      <a href='' className='text-decoration-none text-dark'>Already have an account?</a>
    </form>
  </>
  )
}

export default Register
