import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import { shareBluebookApi } from '../../apis/Api'; // Import your shareBluebookApi function


const ShareSection = () => {
    const [bluebookId, setBluebookId] = useState('');
    const [userId, setUserId] = useState('');

    const handleShareBluebook = async () => {
        try {
            const response = await shareBluebookApi(userId, { bluebookId });
            if (response.data.success) {
                toast.success('Bluebook shared successfully');
            } else {
                toast.error(response.data.message);
            }
        } catch (error) {
            console.error('Error sharing bluebook:', error);
            toast.error('Error sharing bluebook');
        }
    };

    return (
        <div>
            <h2>Share Bluebook</h2>
            <input
                type="text"
                placeholder="Enter Bluebook ID"
                value={bluebookId}
                onChange={(e) => setBluebookId(e.target.value)}
            />
            <input
                type="text"
                placeholder="Enter User ID"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
            />
            <button onClick={handleShareBluebook}>Share Bluebook</button>
            <ToastContainer />
        </div>
    );
};

export default ShareSection;
