import React, { useState, useEffect } from 'react';
import { getSharedBluebooksApi } from '../../apis/Api'; // Import your API function for fetching shared bluebooks
import { ToastContainer, toast } from 'react-toastify';

const ViewSharedBluebook = () => {
    const [sharedBluebooks, setSharedBluebooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const userId = "65d0df4d0b10ef9253e2b36d"; // Replace this with the actual user ID

    useEffect(() => {
        fetchSharedBluebooks();
    }, []);

    const fetchSharedBluebooks = async () => {
        try {
            const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
            const response = await getSharedBluebooksApi(loggedInUserId);
            setSharedBluebooks(response.data.sharedBluebooks);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching shared bluebooks:', error);
            toast.error('Error fetching shared bluebooks');
        }
    };

    return (
        <div className="shared-bluebooks-container">
            <h2 className="shared-bluebooks-title">Shared Bluebooks</h2>
            {loading ? (
                <p>Loading...</p>
            ) : sharedBluebooks.length === 0 ? (
                <div className="no-shared-bluebooks">
                    <p>No shared bluebooks found.</p>
                </div>
            ) : (
                <div>
                    {sharedBluebooks.map(sharedBluebook => (
                        <div key={sharedBluebook._id} className="shared-bluebook-card">
                            <h3 className="bluebook-name">{sharedBluebook.bluebookName}</h3>
                            <p className="bluebook-info">Shared By: {sharedBluebook.sharedBy}</p>
                            {/* Add more details as needed */}
                        </div>
                    ))}
                </div>
            )}
            <ToastContainer />
        </div>
    );
};

export default ViewSharedBluebook;
