import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import { createLicenseApi, getAllLicenseApi } from "../apis/Api";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import "./Dashboard.css"; // Import the CSS file

export const Dashboard = () => {
  const [licenseName, setLicenseName] = useState("");
  const [licenseImage, setLicenseImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [isEditMode, setIsEditMode] = useState(true); // Added state to track edit mode
  const navigate = useNavigate(); // Initialize useNavigate

  const handleImage = (event) => {
    const file = event.target.files[0];
    setLicenseImage(file);
    setPreviewImage(URL.createObjectURL(file));
  };

  const [license, setLicense] = useState([]);
  useEffect(() => {
    getAllLicenseApi().then((res) => {
      setLicense(res.data.license);
    });
  }, []); // Added an empty dependency array to ensure useEffect runs only once

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("licenseName", licenseName);
    formData.append("licenseImageUrl", licenseImage);

    createLicenseApi(formData)
      .then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
          setLicenseName(""); // Clear license name after successful submission
          setLicenseImage(null); // Clear license image after successful submission
          setPreviewImage(null); // Clear preview image after successful submission
          setIsEditMode(false); // Set edit mode to false after saving
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error");
      });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login page after logout
  };
  const Sidebar = ({ setSelectedItem }) => {

    const liStyle ={
      backgroundColor:"white",padding:'0.5rem',marginLeft:"-20px",borderRadius:"10px"
    }

      const [isSidebarOpen, setIsSidebarOpen] = useState(false);

      const handleToggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
      };

      const handleItemClick = (item) => {
        setSelectedItem(item);

        setIsSidebarOpen(false);


      };
      const DashboardSection = ({ selectedItem }) => (
        <section className="home-section">
          {selectedItem === 'dashboard' && <Dashboard />}
          {selectedItem === 'Bluebook' && <Bluebook/>}
          {selectedItem === 'License' && <License/>}
          {selectedItem === 'View Fine' && <ViewFine/>}

        </section>
      );


      return (
        <div className="app-container">
        <Sidebar setSelectedItem={setSelectedItem} />
        <DashboardSection selectedItem={selectedItem} />

        <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`} style={{backgroundColor:"#E1D0C0"}}>
          <div className="logo-details">
            <h1>Drivers App</h1>
            <svg  id="btn" onClick={handleToggleSidebar} xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" class="bi bi-list" viewBox="0 0 16 16">
      <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5m0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5m0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5"/>
    </svg>

          </div>
          <div>

          <ul className="nav-list ">

            <li onClick={() => handleItemClick('dashboard')} className='aside-li pe-auto' style={liStyle}>Home</li>
            <li onClick={() => handleItemClick('bluebook')} className='aside-li pe-auto' style={liStyle}>Bluebook</li>
            <li onClick={() => handleItemClick('License')} className='aside-lipe-auto'style={liStyle} >License</li>
            <li onClick={() => handleItemClick('View Fine')} className='aside-li pe-auto' style={liStyle}>View Fine</li>
          </ul>
          </div>

        </div>
        </div>
      );
    };

  return (
    <>
      <div className="m-4">
        <div className="text-center">
          <button onClick={handleLogout} className="btn btn-danger mb-3">
            Logout
          </button>
          {isEditMode ? (
            <>
              <h1>Dashboard</h1>
              <label className="license-number-label">License Number</label>
              <input
                type="text"
                className="license-number-box"
                value={licenseName}
                onChange={(e) => setLicenseName(e.target.value)}
              />
              <div className="license-image-container">
                {previewImage && (
                  <img
                    src={previewImage}
                    className="license-image"
                    alt="Preview"
                  />
                )}
                <label htmlFor="licenseImage" className="upload-label">
                  {previewImage ? "Change Image" : "Upload Image"}
                </label>
                <input
                  id="licenseImage"
                  onChange={handleImage}
                  type="file"
                  className="upload-input"
                />
              </div>
              <button
                onClick={handleSubmit}
                type="button"
                className="btn btn-primary"
              >
                Save License
              </button>
            </>
          ) : (
            <>
              <h1>Saved License</h1>
              <div className="saved-license-container">
                <p className="license-number">{licenseName}</p>
                {previewImage && (
                  <img
                    src={previewImage}
                    className="license-image"
                    alt="Saved License"
                  />
                )}
              </div>
            </>
          )}
        </div>
        {license.length > 0 && (
          <table className="table mt-4">
            <thead className="table-dark">
              <tr>
                <th scope="col">License Name</th>
                <th scope="col">License Image</th>
              </tr>
            </thead>
            <tbody>
              {license.map((item) => (
                <tr key={item._id}>
                  <td>{item.licenseName}</td>
                  <td className="text-center">
                    <img
                      src={item.licenseImageUrl}
                      height={150}
                      width={120}
                      alt={item.licenseName}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
};
