import axios from "axios";

const Api = axios.create({
    baseURL: "http://localhost:5000",
    withCredentials: true,
});
const config ={
    headers: {
        "Content-Type" : "multipart/form-data"
    }
};

export const registerApi=(data)=>Api.post("/api/user/register",data)
export const loginApi=(data)=>Api.post("/api/user/login",data)

export const forgotPasswordApi = (data) => Api.post("/api/user/forgot/password", data);
export const resetPasswordApi = (data,token) => Api.put(`/api/user/password/reset/${token}`, data);


export const createLicenseApi = (formData) => Api.post('/api/license/create_license',formData)
export const getAllLicenseApi = () => Api.get("/api/license/get_license")

export const verifyEmail = (userId) => Api.get(`/verify?id=${userId}`);

export const createAdminApi = (formData) =>Api.post("/api/admin/create_admin", formData);
export const getAllAdminApi = () =>Api.get("/api/admin/get_admin");
export const getSingleAdminApi = (id) =>Api.get(`/api/admin/get_admin/${id}`);
export const updateAdminApi = (id, formData) =>Api.put(`/api/admin/update_admin/${id}`,formData,config);
export const deleteAdminApi = (id) =>Api.delete(`/api/admin/update_admin/${id}`,formData,config);
