import React, { useState } from "react";
import "./Sidebar.css"; // Import your CSS file if you have one
import Bluebook from "./Bluebook";
import Profile from "../Profile";
import { useNavigate } from "react-router-dom";
import SearchPage from "./ViewFine";
import Notifications from "./ShareBluebook";
import License from "./License";
import Dashboard from "../../dashboard/MainDashboard";
import BluebookComponent from "./BluebookShareBox";

const Sidebar = ({ setSelectedItem }) => {
  const liStyle = {
    backgroundColor: "white",
    padding: "0.5rem",
    marginLeft: "-20px",
    borderRadius: "10px",
  };

  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleToggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
    setIsSidebarOpen(false);
  };

  const navigate = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login page after logout
  };

  return (
    <div className={`sidebar ${isSidebarOpen ? "open" : ""}`}>
      <header className="profile-header">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-md-6">
              <h2 className="profile-title">Welcome, John Doe</h2>
              <p className="profile-subtitle">Your dashboard overview</p>
            </div>
            <div className="col-md-6">
              <div className="profile-avatar">
                <img src="path/to/avatar.jpg" alt="Profile Picture" />
              </div>
            </div>
          </div>
        </div>
      </header>

      <div>
        <ul className="nav-list ">
          <li
            onClick={() => handleItemClick("dashboard")}
            className="aside-li pe-auto d-flex"
            style={liStyle}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              className="bi bi-house-door-fill"
              viewBox="0 0 16 16"
            >
              {/* SVG path for home icon */}
            </svg>
            <span className="px-2">Home</span>
          </li>
          {/* Other list items */}
        </ul>
      </div>

      <button onClick={handleLogout} className="logoutBtn">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="30"
          height="16"
          fill="currentColor"
          className="bi bi-box-arrow-right"
          viewBox="0 0 16 16"
        >
          {/* SVG path for logout icon */}
        </svg>
        Logout
      </button>
    </div>
  );
};

const DashboardSection = ({ selectedItem }) => (
  <section className="home-section">
    {selectedItem === "dashboard" && <Dashboard />}
    {/* Other sections */}
  </section>
);

const MainDashboard = () => {
  const [selectedItem, setSelectedItem] = useState("dashboard");

  return (
    <div className="app-container">
      <Sidebar setSelectedItem={setSelectedItem} />
      <DashboardSection selectedItem={selectedItem} />
    </div>
  );
};

export default MainDashboard;
