// License.js

import React, { useState } from 'react';
import { getAllLicenseApi, deleteLicenseApi } from '../../apis/Api'; // Import the deleteLicenseApi
import { Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './License.css';
import LicenseModel from './LicenseModel';
import ConfirmationMessage from './ConfirmationMessage'; // Import the ConfirmationMessage component

const License = () => {
  const [licenses, setLicenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);
  const [confirmationVisible, setConfirmationVisible] = useState(false); // State to manage visibility of confirmation message
  const [licenseToDelete, setLicenseToDelete] = useState(null); // State to store the license to be deleted

  useEffect(() => {
    fetchLicenses();
  }, []);

  const fetchLicenses = async () => {
    try {
      const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
      const response = await getAllLicenseApi(loggedInUserId);
      setLicenses(response.data.license);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const handleDeleteConfirmation = (license) => {
    setLicenseToDelete(license);
    setConfirmationVisible(true);
  };

  const handleConfirmDelete = async () => {
    try {
      await deleteLicenseApi(licenseToDelete._id);
      toast.success('License deleted successfully');
      setLicenses(licenses.filter((license) => license._id !== licenseToDelete._id));
    } catch (error) {
      console.error('Error deleting license:', error);
      toast.error('Error deleting license');
    } finally {
      setConfirmationVisible(false);
      setLicenseToDelete(null);
    }
  };

  const handleCancelDelete = () => {
    setConfirmationVisible(false);
    setLicenseToDelete(null);
  };

  return (
    <div className="license-container">
      <h2 className="license-title">License Entries</h2>
      {loading ? (
        <p>Loading...</p>
      ) : !licenses || licenses.length === 0 ? (
        <div className="no-licenses">
          <p>No license entries found.</p>
          <Link to="/license/create" className="create-license-link">
            Create License
          </Link>
        </div>
      ) : (
        <div>
          {licenses.map((license) => (
            <div key={license._id} className="license-card">
              <label htmlFor="license-name">License Number: </label>
              <h3 className="license-name">{license.licenseName}</h3>
              <img
                src={license.licenseImageUrl}
                alt="License"
                className="license-image"
                onClick={() => handleImageClick(license.licenseImageUrl)}
              />
              <button onClick={() => handleDeleteConfirmation(license)}>Delete</button>
            </div>
          ))}
          <Link to="/license/create" className="create-license-link">
            Create License
          </Link>
        </div>
      )}
      <ToastContainer />
      {selectedImage && (
        <LicenseModel imageUrl={selectedImage} onClose={handleCloseModal} />
      )}
      {confirmationVisible && (
        <ConfirmationMessage
          message="Are you sure you want to delete this license?"
          onConfirm={handleConfirmDelete}
          onCancel={handleCancelDelete}
        />
      )}
    </div>
  );
};

export default License;
