import axios from "axios";

const Api = axios.create({
    baseURL: "http://localhost:5000",
    withCredentials: true,
    headers: {
        "Content-Type" : "multipart/form-data"
    }
})

export const registerApi=(data)=>Api.post("/api/user/register",data)
export const loginApi=(data)=>Api.post("/api/user/login",data)
export const verifyApi=(data)=>Api.post("/api/user/verify",data)

export const forgetPasswordApi = (data) => Api.post("/api/user/forget/password", data);
export const resetPasswordApi = (data, token) =>Api.put(`/api/user/password/reset/${token}`, data);

export const forgetVerifyApi = (email) => api.post('/forget-verify', { email });
export const resetPassword = (user_id, password) => api.post('/reset-password', { user_id, password });

export const createLicenseApi = (formData) => Api.post('/api/license/create_license',formData)
export const getAllLicenseApi = () => Api.get("/api/license/get_license")
