// License.jsx

import React, { useState, useEffect } from 'react';
import { getAllLicenseApi, createLicenseApi } from '../../apis/Api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './License.css';

const License = () => {
  const [licenses, setLicenses] = useState([]);
  const [licenseName, setLicenseName] = useState('');
  const [licenseImage, setLicenseImage] = useState(null);

  useEffect(() => {
    fetchAllLicenses();
  }, []);

  const fetchAllLicenses = async () => {
    try {
      const response = await getAllLicenseApi();
      setLicenses(response.data.license || []);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  const handleAddLicense = async () => {
    try {
      const formData = new FormData();
      formData.append('licenseName', licenseName);
      formData.append('licenseImage', licenseImage);

      await createLicenseApi(formData);
      toast.success('License added successfully');
      fetchAllLicenses(); // Refresh licenses after adding a new one
      setLicenseName('');
      setLicenseImage(null);
    } catch (error) {
      console.error('Error adding license:', error);
      toast.error('Error adding license');
    }
  };

  return (
    <div className="license-container">
      <h2>License Page</h2>
      <div className="add-license">
        <input
          type="text"
          placeholder="Enter License Number"
          value={licenseName}
          onChange={(e) => setLicenseName(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setLicenseImage(e.target.files[0])}
        />
        <button onClick={handleAddLicense}>Add License</button>
      </div>
      <div className="license-list">
        {licenses.length === 0 ? (
          <p>No licenses stored.</p>
        ) : (
          licenses.map((license) => (
            <div key={license._id} className="license-card">
              <img src={license.licenseImageUrl} alt="License" className="license-image" />
              <p className="license-number">License Number: {license.licenseName}</p>
            </div>
          ))
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default License;
