import React, { useEffect, useState } from 'react';
import { Link,useHistory } from 'react-router-dom';
import { verifyEmail } from '../apis/Api';
import { toast } from 'react-toastify';


const VerificationMessage = () => {
  const [message, setMessage] = useState('');

  useEffect(() => {
    const verifyMail = async () => {
      try {
        const response = await verifyEmail(); // Assuming verifyEmail returns a promise

        if (response.data.success) {
          setMessage('Email has been verified. Redirecting to login page.');
          toast.success('Email has been verified.');
          setTimeout(() => {
            // Redirect to login page using Link
            // Note: This will replace the current component with the login page
            window.location.href = '/login'; 
          }, 3000); // Adjust the delay as needed
        } else {
          setMessage('Email verification failed. Please try again.');
          toast.error('Email verification failed.');
        }
      } catch (error) {
        console.error('Error verifying email:', error.message);
        setMessage('Internal Server Error. Please try again later.');
        toast.error('Internal Server Error.');
      }
    };

    verifyMail();
  }, []);

  return (
    <div className="verification-message">
      <h2>{message}</h2>
      <Link to="/login">Go back to Login</Link>
    </div>
  );
};

export default VerificationMessage;
