import React, { useState, useEffect } from 'react';
import { getSharedBluebooksApi } from '../../apis/Api'; // Import the API function to fetch shared bluebooks
import { ToastContainer, toast } from 'react-toastify';

const SharedBluebooks = () => {
    const [sharedBluebooks, setSharedBluebooks] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchSharedBluebooks();
    }, []);

    const fetchSharedBluebooks = async () => {
        try {
            const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
            const response = await getSharedBluebooksApi(); // Assuming this API function doesn't require a user ID parameter
            setSharedBluebooks(response.data.sharedBluebooks);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching shared bluebooks:', error);
            toast.error('Error fetching shared bluebooks');
        }
    };

    return (
        <div className="shared-bluebooks-container">
            <h2>Shared Bluebooks</h2>
            {loading ? (
                <p>Loading...</p>
            ) : sharedBluebooks.length === 0 ? (
                <p>No shared bluebooks found.</p>
            ) : (
                <ul>
                    {sharedBluebooks.map((bluebook, index) => (
                        <li key={index}>
                            <p>Bluebook Name: {bluebook.bluebookName}</p>
                            <p>Vehicle Number: {bluebook.vehicleNumber}</p>
                            {/* Add other bluebook details here */}
                        </li>
                    ))}
                </ul>
            )}
            <ToastContainer />
        </div>
    );
};

export default SharedBluebooks;
