import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Profile.css'; // Import the CSS file
import { updateUser } from '../apis/Api'; // Import the updateUser function
import { toast } from 'react-toastify'; // Import toast from react-toastify
import dummyPhoto from '../assets/images/user.png'; // Import the dummy photo

const Profile = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [formData, setFormData] = useState({
    firstName: user.firstName,
    lastName: user.lastName,
    email: user.email,
    phoneNo: user.phoneNo,
    avatar: '', // Initially set to empty string
  });

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    console.log('Stored user:', storedUser);
    setUser(storedUser);
    setFormData(prevState => ({
      ...prevState,
      avatar: storedUser.avatar || '', // Set avatar URL from stored user data
    }));
  }, []);

  const handleSubmit = async () => {
    try {
      const response = await updateUser(user._id, formData);
      console.log('Profile updated successfully:', response.data);
      toast.success('Profile updated successfully'); // Show success toast message
    } catch (error) {
      console.error('Error updating profile:', error.message);
      toast.error('Error updating profile'); // Show error toast message
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePhotoChange = async (e) => {
    try {
      const file = e.target.files[0];
      console.log('Selected file:', file);
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = () => {
        console.log('Read file result:', reader.result);
        setFormData({
          ...formData,
          avatar: reader.result,
        });
      };
    } catch (error) {
      console.error('Error updating user profile:', error.message);
      toast.error('Error updating profile'); // Show error toast message
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <Link to="/dashboards" className="back-button">&#8249; Back</Link>
        <h2 className="profile-title">Profile</h2>
      </div>
      <div className="profile-info">
        <div className="profile-photo">
          <img src={formData.avatar || dummyPhoto} alt="Profile" className="profile-photo-image" /> {/* Use avatar URL if available, otherwise show dummy photo */}
          <input type="file" onChange={handlePhotoChange} accept="image/*" />
        </div>
        <div className="profile-fields">
          <div className="profile-field">
            <label className="profile-label">First Name:</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Email:</label>
            <div className="profile-value">{formData.email}</div>
          </div>
          <div className="profile-field">
            <label className="profile-label">Phone Number:</label>
            <input
              type="tel"
              name="phoneNo"
              value={formData.phoneNo}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Status:</label>
            <div className="profile-value">{user.is_verified ? 'Verified' : 'Not Verified'}</div>
          </div>
        </div>
      </div>

      <button className="profile-button" onClick={handleSubmit}>
        Save Changes
      </button>
    </div>
  );
};

export default Profile;
