// License.js

import React, { useState, useEffect } from 'react';
import { getLicenseApi, deleteLicenseApi } from '../apis/Api';
import { toast } from 'react-toastify';
import './License.css';

const License = () => {
  const [licenses, setLicenses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchLicenses();
  }, []);

  const fetchLicenses = async () => {
    try {
      const response = await getLicenseApi();
      setLicenses(response.data.licenses);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  const handleDeleteLicense = async (licenseId) => {
    try {
      await deleteLicenseApi(licenseId);
      setLicenses(prevLicenses => prevLicenses.filter(license => license._id !== licenseId));
      toast.success('License deleted successfully');
    } catch (error) {
      console.error('Error deleting license:', error);
      toast.error('Error deleting license');
    }
  };

  return (
    <div className="license-container">
      <h2 className="license-title">License</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul className="license-list">
          {licenses.map(license => (
            <li key={license._id} className="license-item">
              <img src={license.licenseImageUrl} alt="License" className="license-image" />
              <div className="license-info">
                <p className="license-name">{license.licenseName}</p>
              </div>
              <div className="license-actions">
                <button className="delete-license-btn" onClick={() => handleDeleteLicense(license._id)}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default License;
