// components/ForgetPassword.js
import React, { useState } from 'react';
import { forgetPasswordApi } from '../apis/Api'; // Adjust this import based on your actual API file
import { toast } from 'react-toastify';

const ForgetPassword = () => {
  const [email, setEmail] = useState('');

  const handleForgetPassword = async () => {
    try {
      const response = await forgetPasswordApi(email);
      if (response.data.success) {
        toast.success(response.data.message);
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      console.error(error);
      toast.error('Internal Server Error.');
    }
  };

  return (
    <div>
      <h2>Forget Password</h2>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button onClick={handleForgetPassword}>Submit</button>
    </div>
  );
};

export default ForgetPassword;
