import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Bluebook from '../pages/userDashboard/Bluebook';
import License from '../pages/userDashboard/License';
import { getAllFollowedUsersApi } from "../apis/Api";
import './MainDashboard.css';

const Dashboard = () => {
  const [followedUsers, setFollowedUsers] = useState([]);
  useEffect(() => {
    const fetchFollowedUsers = async () => {
      try {
        const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
        const response = await getAllFollowedUsersApi(loggedInUserId);
        setFollowedUsers(response.data.followedUsers);
      } catch (error) {
        console.error("Error fetching followed users:", error.message);
      }
    };

    fetchFollowedUsers();
  }, []);
  const loggedInUserId = JSON.parse(localStorage.getItem("user"))._id;

  return (
    <div className="dashboard-container">
      <div className="main-content">
        <div className="bluebook-section">
          <Bluebook />
          <Link to="/bluebook" className="create-bluebook-button">
            Create Bluebook
          </Link>
        </div>
        <div className="license-section">
          <License />
          <Link to="/license" className="create-license-button">
            Add License
          </Link>
        </div>
      </div>
      <div className="side-content">
        <div className="user-profile-section">
          {/* User profile section with followed users */}
          <h3>Users You Follow</h3>
          {followedUsers && followedUsers.length > 0 ? (
            followedUsers.map((user) => (
              <div key={user._id} className="search-result-item">
                <div className="search-result-details">
                  <h5>{user.firstName}</h5>
                  <h5>{user.lastName}</h5>
                  <p>{user.email}</p>
                </div>
                {/* Add any additional details you want to display */}
                </div>
            ))
          ) : (
            <p>No users followed</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
