import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Bluebook from '../pages/userDashboard/Bluebook';
import License from '../pages/userDashboard/License';
import { getAllFollowedUsersApi } from "../apis/Api";
import './MainDashboard.css';

const Dashboard = () => {
  const [followedUsers, setFollowedUsers] = useState([]);
  const [greeting, setGreeting] = useState('');
  const [userName, setUserName] = useState('');
  const [bluebooks, setBluebooks] = useState([]);

  useEffect(() => {
    const fetchFollowedUsers = async () => {
      try {
        const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
        const response = await getAllFollowedUsersApi(loggedInUserId);
        setFollowedUsers(response.data.followedUsers);
      } catch (error) {
        console.error("Error fetching followed users:", error.message);
      }
    };

    fetchFollowedUsers();
  }, []);

  useEffect(() => {
    // Fetch user's name from local storage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUserName(storedUser.firstName); // Assuming the first name is stored
    }

    // Set the greeting
    const currentDate = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    const formattedDate = currentDate.toLocaleDateString(undefined, options);
    setGreeting(`Today is ${formattedDate}`);
  }, []);

  useEffect(() => {
    // Here you would fetch bluebooks and set the bluebooks state
    // Replace the code below with your actual logic to fetch bluebooks
    const fetchBluebooks = async () => {
      // Assuming you have a function to fetch bluebooks from an API
      // Replace 'fetchBluebooksFromApi' with your actual function
      const bluebooksFromApi = await fetchBluebooksFromApi();
      setBluebooks(bluebooksFromApi);
    };

    fetchBluebooks();
  }, []);

  // Function to fetch bluebooks from API (Replace with actual fetch logic)
  const fetchBluebooksFromApi = async () => {
    // Sample data for demonstration
    return [
      { id: 1, title: "Bluebook 1" },
      { id: 2, title: "Bluebook 2" },
      { id: 3, title: "Bluebook 3" },
    ];
  };

  return (
    <div className="dashboard-container">
      <div className="greeting-section">
        <div className="greeting">{greeting}</div>
        <div className="username">Hi, {userName}</div>
      </div>
      <div className="content">
        <div className="main-content">
          <div className="bluebook-section">
            {bluebooks.slice(0, 1 ).map((bluebook) => (
              <div key={bluebook.id}>
                <Bluebook />
              </div>
            ))}
            {bluebooks.length > 1 && (
              <Link to="/bluebook" className="view-more-bluebooks">
                View More
              </Link>
            )}
          </div>
          <div className="license-section">
            <License />
          </div>
        </div>
        <div className="side-content">
          <div className="user-profile-section">
            {/* User profile section with followed users */}
            <h3>Users You Follow</h3>
            {followedUsers && followedUsers.length > 0 ? (
              followedUsers.map((user) => (
                <div key={user._id} className="search-result-item">
                  <div className="search-result-details">
                    <h5>{user.firstName}</h5>
                    <h5>{user.lastName}</h5>
                    <p>{user.email}</p>
                  </div>
                  {/* Add any additional details you want to display */}
                </div>
              ))
            ) : (
              <p>No users followed</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
