import React, { useState, useEffect } from "react";
import { followUserApi, searchUserApi } from "../../apis/Api";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [searchMessage, setSearchMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = () => {
    searchUserApi({ phoneNo: searchQuery })
      .then((res) => {
        setSearchResult(res.data.users[0]); // Assuming only one user is returned
        // Check if the user is already followed
        setFollowStatus(res.data.users[0]?.isFollowed || false);
      })
      .catch((error) => {
        console.error("Error searching user:", error);
        setSearchResult(null);
        setFollowStatus(false); // Reset follow status if search fails
      });
  };

  const handleFollow = () => {
    // Toggle follow status
    setFollowStatus(!followStatus);

    // Make API call to follow/unfollow user
    const userId = searchResult._id; // Assuming searchResult contains user data
    followUserApi(userId)
      .then((res) => {
        console.log("Followed user successfully");
        // Show success toast message
        toast.success('User followed successfully');
      })
      .catch((error) => {
        console.error("Error following user:", error);
        // Show error toast message
        toast.error('Failed to follow user');
        // Reset follow status to previous state
        setFollowStatus(!followStatus);
      });
  };

  return (
    <div>
      <h1>Search Page</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Enter search query"
      />
      <button onClick={handleSearchSubmit}>Search</button>

      {searchResult && (
        <div>
          <h2>User Details</h2>
          <p>Name: {searchResult.firstName} {searchResult.lastName}</p>
          <p>Phone Number: {searchResult.phoneNo}</p>

          {followStatus ? (
            <button onClick={handleFollow}>Following</button>
          ) : (
            <button onClick={handleFollow}>Follow</button>
          )}
        </div>
      )}

      {/* Toast container for displaying toast messages */}
      <ToastContainer />
    </div>
  );
};

export default SearchPage;
