import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { getSingleAdminApi, updateAdminApi } from "../../apis/Api";
import { toast } from "react-toastify";
const AdminEdit = () => {
  //Receive doctor id from URL
  const { id } = useParams();

  //UseEffect
  useEffect(() => {
    getSingleAdminApi(id).then((res) => {
      console.log(res.data);
      setfirstName(res.data.admin.firstName);
      setlastName(res.data.admin.lastName);
      setEmail(res.data.admin.email);
      setPhoneNo(res.data.admin.phoneNo);
      setOldImage(res.data.doctor.uploadValidIdUrl);
    });
  }, [id]);

  //Make UseState
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNo, setPhoneNo] = useState("");
  const [oldImage, setOldImage] = useState("");

  //Make UseState for image
  const [uploadValidId, setValidId] = useState(null);
  const [previewId, setPreviewId] = useState(null);

  //Image Upload function
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    console.log(file);
    setValidId(file);
    setPreviewId(URL.createObjectURL(file));
  };

  //handle submit function
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("firstName", firstName);
    formData.append("lastName", lastName);
    formData.append("email", email);
    formData.append("phoneNo", phoneNo);
    formData.append("uploadValidId", uploadValidId);

    //make a api call
    updateAdminApi(id, formData)
      .then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
          navigate("/admin/dashboard");
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal server Error!");
      });
  };

  return (
    <>
      <section class="vh-100 ">
        <div class="container py-5 d-flex justify-content-center">
          <div className="col col-md-10 col-sm-12 col-lg-8">
            <div
              class="card d-flex align-items-center shadow-lg"
              style={{
                background: "linear-gradient(to top, #ffffff, #61C0BF)",
              }}
            >
              <div class="col-md-6 col-lg-9 d-flex align-items-center">
                <div class="card-body p-8 p-lg-5 text-black">
                  <form>
                    <div class=" mb-9 pb-10 ">
                      <i class="fas fa-cubes fa-2x me-2 d-flex justify-content-center"></i>
                    </div>

                    <h3 class="fw-bold d-flex justify-content-center">
                      Editing Details -{" "}
                      <span className="text-danger">{firstName}</span>
                    </h3>

                    <div className="modal-body">
                      <form action="">
                        {/* //1st row */}
                        <div className="row">
                          <div className="col">
                            <label>First Name</label>
                            <input
                              value={firstName}
                              onChange={(e) => setfirstName(e.target.value)}
                              className="form-control mb-2"
                              type="text"
                              name=""
                              id=""
                              placeholder="Enter First Name"
                              style={{ width: "200px" }}
                            />
                          </div>
                          <div className="col">
                            <label>Last Name</label>
                            <input
                              value={lastName}
                              onChange={(e) => setlastName(e.target.value)}
                              className="form-control mb-2"
                              type="text"
                              name=""
                              id=""
                              placeholder="Enter Last Name"
                              style={{ width: "200px" }}
                            />
                          </div>
                          <div className="col">
                            <label>Email</label>
                            <input
                              value={email}
                              onChange={(e) => setEmail(e.target.value)}
                              className="form-control mb-2"
                              type="email"
                              name=""
                              id=""
                              placeholder="Enter your Email"
                              style={{ width: "200px" }}
                            />
                          </div>
                        </div>
                          <div className="col">
                            <label>Phone Number</label>
                            <input
                              value={phoneNo}
                              onChange={(e) => setPhoneNo(e.target.value)}
                              className="form-control mb-2"
                              type="number"
                              name=""
                              id=""
                              placeholder="Enter Your Phone Number"
                              style={{ width: "200px" }}
                            />
                          </div>
                          <label>Upload Valid Id</label>
                          <input
                            onChange={handleImageUpload}
                            className="form-control"
                            type="file"
                            name=""
                            id=""
                            placeholder="Enter Full Name"
                            style={{ width: "490px" }}
                          />

                        <div class="pt-1 mb-4 d-flex text-center justify-content-center">
                          <button
                            onClick={handleSubmit}
                            className="btn w-50 mb-2 mt-2 btn btn-dark"
                          >
                            Update Admin
                          </button>
                        </div>
                      </form>
                      <div>
                        <h6>Old Image Preview</h6>
                        <img
                          className="img-fluid rounded-4 object-fit-cover"
                          width={200}
                          height={200}
                          src={oldImage}
                          alt=""
                        />
                        <h6 className="mt-4">New Image</h6>
                        {previewId ? (
                          <img
                            src={previewId}
                            alt="Admin Image"
                            className="img-fluid rounded-4 object-fit-cover"
                            width={200}
                            height={200}
                          />
                        ) : (
                          <p>No Image Selected</p>
                        )}
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default AdminEdit;
