import React, { useState, useEffect, useCallback } from 'react';
import Slide1 from "../assets/images/slide1.jpg";
import Slide2 from "../assets/images/slide2.png";
// import Slide3 from "../assets/images/slide3.jpg";


const Slider = () => {
  const slideInterval = 3000; // 3 seconds
  const slidesData = [
    { src: Slide1, alt: 'Slide 1' },
    { src: Slide2, alt: 'Slide 2' },
    // { src: Slide3, alt: 'Slide 3' },
  ];

  const [currentSlide, setCurrentSlide] = useState(0);
  const [timer, setTimer] = useState(null);

  const showSlide = useCallback((slideIndex) => {
    const slides = document.querySelectorAll('.slide');
    slides.forEach((slide, index) => {
      if (index === slideIndex) {
        slide.classList.add('show-slide');
      } else {
        slide.classList.remove('show-slide');
      }
    });

    const dots = document.querySelectorAll('.dot');
    dots.forEach((dot, index) => {
      if (index === slideIndex) {
        dot.classList.add('active');
      } else {
        dot.classList.remove('active');
      }
    });
  }, []);

  const nextSlide = useCallback(() => {
    const nextSlideIndex = (currentSlide + 1) % slidesData.length;
    setCurrentSlide(nextSlideIndex);
    showSlide(nextSlideIndex);
  }, [currentSlide, slidesData.length, showSlide]);

  const resetTimer = useCallback(() => {
    if (timer) {
      clearInterval(timer);
    }
    setTimer(setInterval(nextSlide, slideInterval));
  }, [timer, nextSlide, slideInterval]);

  useEffect(() => {
    showSlide(currentSlide);
    resetTimer();

    return () => {
      clearInterval(timer);
    };
  }, [currentSlide, resetTimer, timer, showSlide]);

  const dots = slidesData.map((slide, index) => (
    <div
      key={index}
      className={`dot ${index === currentSlide ? 'active' : ''}`}
      data-slide-index={index}
      onClick={() => {
        setCurrentSlide(index);
        resetTimer();
      }}
    ></div>
  ));

  return (
    <div className="containers">
      <div className="slider">
        {slidesData.map((slide, index) => (
          <img
            key={index}
            className={`slide ${index === 0 ? 'show-slide' : ''}`}
            src={slide.src}
            alt={slide.alt}
          />
        ))}
      </div>
      <div className="dots-container">{dots}</div>
    </div>
  );
};

export default Slider;
