// Profile.js
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { updateUser } from './api'; // Import the API request function

const Profile = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    firstName: user.firstName,
    lastName: user.lastName,
    email: user.email,
    phoneNo: user.phoneNo,
  });
  const history = useHistory();

  const handleEditClick = () => {
    setEditMode(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedUser = await updateUser(user._id, formData);
      setUser(updatedUser.user);
      setEditMode(false);
      localStorage.setItem('user', JSON.stringify(updatedUser.user));
      alert('User updated successfully');
    } catch (error) {
      console.error('Error updating user:', error);
      alert('Error updating user. Please try again.');
    }
  };

  const handleCancel = () => {
    setEditMode(false);
    // Reset form data to original user data
    setFormData({
      firstName: user.firstName,
      lastName: user.lastName,
      email: user.email,
      phoneNo: user.phoneNo,
    });
  };

  return (
    <div className="profile-container">
      <h2 className="profile-title">Profile</h2>
      {editMode ? (
        <form onSubmit={handleSubmit}>
          <div className="profile-fields">
            <div className="profile-field">
              <label className="profile-label">First Name:</label>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
            </div>
            <div className="profile-field">
              <label className="profile-label">Last Name:</label>
              <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
            </div>
            <div className="profile-field">
              <label className="profile-label">Email:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </div>
            <div className="profile-field">
              <label className="profile-label">Phone Number:</label>
              <input type="text" name="phoneNo" value={formData.phoneNo} onChange={handleChange} required />
            </div>
          </div>
          <div className="profile-buttons">
            <button type="submit">Save</button>
            <button type="button" onClick={handleCancel}>Cancel</button>
          </div>
        </form>
      ) : (
        <div className="profile-info">
          <div className="profile-field">
            <label className="profile-label">First Name:</label>
            <div className="profile-value">{user.firstName}</div>
          </div>
          <div className="profile-field">
            <label className="profile-label">Last Name:</label>
            <div className="profile-value">{user.lastName}</div>
          </div>
          <div className="profile-field">
            <label className="profile-label">Email:</label>
            <div className="profile-value">{user.email}</div>
          </div>
          <div className="profile-field">
            <label className="profile-label">Phone Number:</label>
            <div className="profile-value">{user.phoneNo}</div>
          </div>
          <button className="edit-profile-button" onClick={handleEditClick}>Edit Profile</button>
        </div>
      )}
    </div>
  );
};

export default Profile;
