import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Bluebook from '../pages/userDashboard/Bluebook';
import License from '../pages/userDashboard/License';
import SharedBluebooks from '../../pages/userDashboard/SharedBluebooks'; // Import the SharedBluebooks component
import { getAllFollowedUsersApi, getAllFollowingUsersApi } from "../apis/Api";
import './MainDashboard.css';

const Dashboard = () => {
  const [followedUsers, setFollowedUsers] = useState([]);
  const [followingUsers, setFollowingUsers] = useState([]);
  const [greeting, setGreeting] = useState('');
  const [userName, setUserName] = useState('');

  useEffect(() => {
    const fetchFollowedUsers = async () => {
      try {
        const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
        const response = await getAllFollowedUsersApi(loggedInUserId);
        setFollowedUsers(response.data.followedUsers);
      } catch (error) {
        console.error("Error fetching followed users:", error.message);
      }
    };

    fetchFollowedUsers();
  }, []);

  useEffect(() => {
    const fetchFollowingUsers = async () => {
      try {
        const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
        const response = await getAllFollowingUsersApi(loggedInUserId);
        setFollowingUsers(response.data.followedUsers);
      } catch (error) {
        console.error("Error fetching followed users:", error.message);
      }
    };

    fetchFollowingUsers();
  }, []);

  useEffect(() => {
    // Fetch user's name from local storage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUserName(storedUser.firstName); // Assuming the first name is stored
    }

    // Set the greeting
    const currentDate = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    const formattedDate = currentDate.toLocaleDateString(undefined, options);
    setGreeting(`Today is ${formattedDate}`);
  }, []);

  return (
    <div className="dashboard-container">
      <div className="greeting-section">
        <div className="greeting">{greeting}</div>
        <div className="username">Hi, {userName}</div>
      </div>
      <div className="content">
        <div className="main-content">
          <div className="bluebook-section">
            <Bluebook />
          </div>
          <div className="license-section">
            <License />
          </div>
        </div>
        <div className="side-content">
          <div className="user-profile-section">
            <h3 tex>Follow</h3>
            {/* Content for the Follow section */}
          </div>
          <div className="user-profile-section2">
            <h3>Following</h3>
            {/* Content for the Following section */}
          </div>
          <div className="view-shared-bluebooks-section"> {/* Added a container for the View Shared Bluebooks */}
            <h3>View Shared Bluebooks</h3>
            <SharedBluebooks /> {/* Placed the SharedBluebooks component here */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
