// License.jsx

import React, { useState, useEffect } from 'react';
import { getAllLicenseApi } from '../../apis/Api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './License.css';

const License = () => {
  const [licenses, setLicenses] = useState([]);

  useEffect(() => {
    fetchAllLicenses();
  }, []);

  const fetchAllLicenses = async () => {
    try {
      const response = await getAllLicenseApi();
      setLicenses(response.data.license);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  return (
    <div className="license-container">
      <h2>My Licenses</h2>
      <div className="license-list">
        {licenses.length === 0 ? (
          <div className="no-license">
            <p>No licenses stored.</p>
            <button className="create-license-btn">Create New License</button>
          </div>
        ) : (
          licenses.map(license => (
            <div key={license._id} className="license-card">
              <img src={license.licenseImageUrl} alt="License" className="license-image" />
              <p className="license-number">License Number: {license.licenseName}</p>
            </div>
          ))
        )}
      </div>
      <ToastContainer />
    </div>
  );
};

export default License;
