import React, { useState } from 'react';
import { resetPasswordApi } from '../apis/Api';
import { toast } from 'react-toastify';

const ResetPassword = ({ match }) => {
  const [password, setPassword] = useState('');

  const handleChange = (e) => {
    setPassword(e.target.value);
  };

 const handleSubmit = (e) => {
  e.preventDefault();
  
  if (match && match.params) {
    const token = match.params.token;
    console.log('Token:', token); // Check if token is being received correctly

    resetPasswordApi({ password, token })
      .then((res) => {
        console.log('Response:', res.data); // Check the response from the API
        toast.success(res.data.message);
        // Optionally, you can redirect the user to the login page or another route
      })
      .catch((err) => {
        console.error('Error:', err.response.data); // Check the error response
        toast.error(err.response.data.message);
      });
  }
};


  return (
    <div>
      <h2>Reset Password</h2>
      <form onSubmit={handleSubmit}>
        <input
        name='password'
          type="password"
          placeholder="Enter your new password"
          value={password}
          onChange={handleChange}
        />
        <button type="submit">Reset Password</button>
      </form>
    </div>
  );
};

export default ResetPassword;
