import axios from "axios";

const Api = axios.create({
    baseURL: "http://localhost:5000",
    withCredentials: true,
    headers: {
        "Content-Type" : "application/json"
    }
})
export const verifyEmail = async (userId) => {
    try {
      const response = await Api.get(`/verify?id=${userId}`);
      return response.data;
    } catch (error) {
      throw error;
    }
  };

export const registerApi=(data)=>Api.post("/api/user/register",data)
export const loginApi=(data)=>Api.post("/api/user/login",data)

export const forgetPasswordApi = (data) => Api.post("/api/user/forget-password", data);
export const resetPasswordApi = (data) => Api.post("/api/user/reset-password", data);


export const createLicenseApi = (formData) => Api.post('/api/license/create_license',formData)
export const getAllLicenseApi = () => Api.get("/api/license/get_license")
