import React, { useState, useEffect } from 'react';
import { getAllBluebookApi, getAllFollowedUsersApi, followUserApi, shareBluebookApi } from './api'; // Update with your actual API file

const ShareSection = () => {
  const [myBluebooks, setMyBluebooks] = useState([]);
  const [usersIFollow, setUsersIFollow] = useState([]);
  const [selectedBluebook, setSelectedBluebook] = useState('');
  const [selectedUser, setSelectedUser] = useState('');

  useEffect(() => {
    // Fetch my bluebooks
    getAllBluebookApi()
      .then(response => {
        setMyBluebooks(response.data.bluebooks);
      })
      .catch(error => console.error('Error fetching my bluebooks:', error));

    // Fetch users I follow
    getAllFollowedUsersApi()
      .then(response => {
        setUsersIFollow(response.data.followedUsers);
      })
      .catch(error => console.error('Error fetching users I follow:', error));
  }, []);

  const handleShareBluebook = () => {
    if (!selectedBluebook || !selectedUser) {
      alert('Please select a bluebook and a user to share with.');
      return;
    }

    // Check if the selected user follows me
    const selectedUserObject = usersIFollow.find(user => user._id === selectedUser);
    if (!selectedUserObject) {
      alert('Selected user does not follow you back. Bluebook cannot be shared.');
      return;
    }

    // Send request to share bluebook
    shareBluebookApi(selectedUser, selectedBluebook)
      .then(response => {
        alert('Bluebook shared successfully!');
      })
      .catch(error => console.error('Error sharing bluebook:', error));
  };

  return (
    <div>
      <div>
        <h3>My Bluebooks</h3>
        {myBluebooks.map(bluebook => (
          <div key={bluebook._id}>
            <p>{bluebook.bluebookName}</p>
            <button onClick={() => setSelectedBluebook(bluebook._id)}>Share</button>
          </div>
        ))}
      </div>
      <div>
        <h3>Users You Follow Bluebooks</h3>
        {usersIFollow.map(user => (
          <div key={user._id}>
            <p>{user.firstName} {user.lastName}</p>
            {/* Display bluebooks shared by this user */}
            {/* You need to fetch and display bluebooks shared by this user */}
          </div>
        ))}
      </div>
      <div>
        <h3>Share Bluebook</h3>
        <select value={selectedBluebook} onChange={e => setSelectedBluebook(e.target.value)}>
          <option value="">Select Bluebook</option>
          {myBluebooks.map(bluebook => (
            <option key={bluebook._id} value={bluebook._id}>{bluebook.bluebookName}</option>
          ))}
        </select>
        <select value={selectedUser} onChange={e => setSelectedUser(e.target.value)}>
          <option value="">Select User</option>
          {usersIFollow.map(user => (
            <option key={user._id} value={user._id}>{user.firstName} {user.lastName}</option>
          ))}
        </select>
        <button onClick={handleShareBluebook}>Share</button>
      </div>
    </div>
  );
};

export default ShareSection;
