import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { viewUserProfileApi, deleteUserApi, deleteUserLicenseApi } from "../apis";

function UserProfile() {
  const { id } = useParams();
  const [user, setUser] = useState({});
  const [license, setLicense] = useState({});

  useEffect(() => {
    // Fetch user profile and license details from the backend API
    // Replace the API endpoints with your actual endpoints
    const fetchData = async () => {
      try {
        const response = await viewUserProfileApi(id);
        const { user, license } = response.data.data;
        setUser(user);
        setLicense(license);
      } catch (error) {
        console.error("Error fetching user profile:", error.message);
      }
    };

    fetchData();
  }, [id]);

  const handleDeleteUser = async () => {
    try {
      await deleteUserApi(id);
      console.log("User deleted successfully");
      // Optionally, you can redirect to another page or perform additional actions after deletion
    } catch (error) {
      console.error("Error deleting user:", error.message);
    }
  };

  const handleDeleteUserLicense = async () => {
    try {
      await deleteUserLicenseApi(license._id);
      console.log("License deleted successfully");
      // Optionally, you can update the user's license list or perform additional actions after license deletion
    } catch (error) {
      console.error("Error deleting license:", error.message);
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      <h2>User Details</h2>
      <p>
        Name: {user.firstName} {user.lastName}
        <br />
        Email: {user.email}
        <br />
        Phone Number: {user.phoneNo}
      </p>
      <h2>License Details</h2>
      {license ? (
        <div>
          License Name: {license.licenseName}
          <br />
          <img src={license.licenseImageUrl} alt="License" style={{ maxWidth: "300px" }} />
          <br />
          <button onClick={handleDeleteUserLicense}>Delete License</button>
        </div>
      ) : (
        <p>No license details available</p>
      )}
      <br />
      <button onClick={handleDeleteUser}>Delete User</button>
    </div>
  );
}

export default UserProfile;
