import React, { useState } from "react";
import { toast } from "react-toastify";
import { searchUserApi, followUserApi } from "../apis/Api";
import "./Dashboard.css";

const Dashboard = () => {
  const [searchText, setSearchText] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [followedUsers, setFollowedUsers] = useState([]);

  const handleSearch = async () => {
    if (searchText.trim() !== "") {
      try {
        const response = await searchUserApi({ phoneNo: searchText });
        setSearchResults(response.data.users);
      } catch (error) {
        console.error("Error searching for user:", error);
        toast.error("An error occurred while searching for the user.");
      }
    }
  };

  const handleFollow = async (userId) => {
    try {
      const response = await followUserApi(userId);
      if (response.data.success) {
        // Update followedUsers state or perform any necessary UI updates
        toast.success(response.data.message);
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      console.error("Error following user:", error);
      toast.error("An error occurred while following the user.");
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <div className="search-container">
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Enter phone number"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {searchResults.length > 0 && (
        <div className="search-results">
          <h2>Search Results:</h2>
          <ul>
            {searchResults.map((user) => (
              <li key={user._id}>
                {user.firstName} {user.lastName}
                <button onClick={() => handleFollow(user._id)}>Follow</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
