import React, { useState } from "react";
import SearchResultBox from "../../dashboard/SearchBoxResult";
import { searchUserApi, followUserApi } from "../apis/Api";

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [followedUserId, setFollowedUserId] = useState(null);

  // Function to handle search query change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to handle search submission
  const handleSearchSubmit = () => {
    searchUserApi({ phoneNo: searchQuery })
      .then((res) => {
        setSearchResults(res.data.users);
      })
      .catch((error) => {
        console.error("Error searching users:", error);
      });
  };

  // Function to handle follow action
  const handleFollow = (userId) => {
    followUserApi(userId)
      .then((res) => {
        setFollowedUserId(userId);
        // Optionally, you can update the search results after following the user
        // For example, remove the followed user from the search results
        setSearchResults(searchResults.filter((user) => user._id !== userId));
      })
      .catch((error) => {
        console.error("Error following user:", error);
      });
  };

  return (
    <div>
      <h1>Search Page</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Enter search query"
      />
      <button onClick={handleSearchSubmit}>Search</button>
      <div className="search-results">
        {searchResults.map((user) => (
          <SearchResultBox key={user._id} user={user} onFollow={handleFollow} />
        ))}
      </div>
      {followedUserId && <p>Followed user with ID: {followedUserId}</p>}
    </div>
  );
};

export default SearchPage;
