// License.js

import React, { useState, useEffect } from 'react';
import { getAllLicenseApi, deleteLicenseApi } from '../../apis/Api'; // Assuming you have an API function for deleting a license
import { Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './License.css';
import LicenseModel from './LicenseModel';

const License = () => {
  const [licenses, setLicenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    fetchLicenses();
  }, []);

  const fetchLicenses = async () => {
    try {
      const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
      const response = await getAllLicenseApi(loggedInUserId);
      setLicenses(response.data.license);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  const handleImageClick = (imageUrl) => {
    setSelectedImage(imageUrl);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const handleDeleteLicense = async (licenseId) => {
    // Display a confirmation dialog
    const confirmed = window.confirm("Are you sure you want to delete this license?");
    
    // If the user confirms the deletion, proceed with the deletion process
    if (confirmed) {
      try {
        await deleteLicenseApi(licenseId);
        toast.success('License deleted successfully');
        // Refresh the license list after deletion
        fetchLicenses();
      } catch (error) {
        console.error('Error deleting license:', error);
        toast.error('Error deleting license. Please try again.');
      }
    }
  };
  

  return (
    <div className="license-container">
      <h2 className="license-title">License Entries</h2>
      {loading ? (
        <p>Loading...</p>
      ) : !licenses || licenses.length === 0 ? (
        <div className="no-licenses">
          <p>No license entries found.</p>
          <Link to="/license/create" className="create-license-link">
            Create License
          </Link>
        </div>
      ) : (
        <div>
          {licenses.map((license) => (
            <div key={license._id} className="license-card">
              <label htmlFor="license-name">License Number: </label>
              <h3 className="license-name">{license.licenseName}</h3>
              <img
                src={license.licenseImageUrl}
                alt="License"
                className="license-image"
                onClick={() => handleImageClick(license.licenseImageUrl)}
              />
              <button onClick={() => handleDeleteLicense(license._id)}>Delete</button>
            </div>
          ))}
          <Link to="/license/create" className="create-license-link">
            Create License
          </Link>
        </div>
      )}
      <ToastContainer />
      {selectedImage && (
        <LicenseModel imageUrl={selectedImage} onClose={handleCloseModal} />
      )}
    </div>
  );
};

export default License;
