import React, { useState, useEffect } from "react";
import {
  createAdminApi,
  getAllAdminApi,
  deleteAdminApi,
} from "../../apis/Api";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";

const AdminDashboard = () => {
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const [email, setEmail] = useState("");
  const [phoneNo, setPhoneNo] = useState("");
  const [uploadValidId, setValidId] = useState(null);
  const [previewId, setPreviewId] = useState(null);
  const [admin, setAdmin] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getAllAdminApi()
      .then((res) => {
        setAdmin(res.data.doctors);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching admin data:", error);
        setLoading(false);
      });
  }, []);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    console.log(file);
    setValidId(file);
    setPreviewId(URL.createObjectURL(file));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("firstName", firstName);
    formData.append("lastName", lastName);
    formData.append("email", email);
    formData.append("phoneNo", phoneNo);
    formData.append("uploadValidId", uploadValidId);

    console.log(formData);

    createAdminApi(formData)
      .then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error!");
      });
  };

  const handleDelete = (id) => {
    const confirm = window.confirm("Are you sure you want to delete this id?");
    if (!confirm) {
      return;
    } else {
      deleteAdminApi(id)
        .then((res) => {
          if (res.data.success === false) {
            toast.error(res.data.message);
          } else {
            toast.success(res.data.message);
            window.location.reload();
          }
        })
        .catch((error) => {
          console.error("Error deleting admin:", error);
        });
    }
  };

  return (
    <>
      <div className="m-4">
        <div className="d-flex justify-content-between">
          <h3>Welcome, Admin</h3>
          <button
            type="button"
            className="btn w-20 mb-2 btn btn-dark"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"
          >
            Add Admin
          </button>
          <div
            className="modal fade"
            id="exampleModal"
            tabIndex="-1"
            aria-labelledby="exampleModalLabel"
            aria-hidden="true"
          >
 <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h1 className="modal-title fs-5" id="exampleModalLabel">
                    Add new admin!
                  </h1>
                  <button
                    type="button"
                    className="btn-close"
                    data-bs-dismiss="modal"
                    aria-label="Close"
                  ></button>
                </div>
                <div className="modal-body">
                  <label>First Name</label>
                  <input
                    onChange={(e) => setfirstName(e.target.value)}
                    className="form-control mb-2"
                    type="text"
                    name=""
                    id=""
                    placeholder=""
                  />
                  <label>Last Name</label>
                  <input
                    onChange={(e) => setlastName(e.target.value)}
                    className="form-control mb-2"
                    type="text"
                    name=""
                    id=""
                    placeholder=""
                  />


                  <label>Email</label>
                  <input
                    onChange={(e) => setEmail(e.target.value)}
                    className="form-control mb-2"
                    type="email"
                    name=""
                    id=""
                    placeholder=""
                  />

                  <label>Phone Number</label>
                  <input
                    onChange={(e) => setPhoneNo(e.target.value)}
                    className="form-control mb-2"
                    type="number"
                    name=""
                    id=""
                    placeholder=""
                  />
                  <label>Upload Valid Id</label>
                  <input
                    onChange={handleImageUpload}
                    className="form-control mb-2"
                    type="file"
                    name=""
                    id=""
                    placeholder="Enter Full Name"
                  />
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-secondary"
                    data-bs-dismiss="modal"
                  >
                    Close
                  </button>
                  <button
                    onClick={handleSubmit}
                    type="button"
                    className="btn btn-primary"
                  >
                    Save changes
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <table className="table mt-2 table-bordered">
        <thead className="table-dark">
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Phone Number</th>
            <th>Email</th>
            <th>Valid Id</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan="5">Loading...</td>
            </tr>
          ) : admin.length > 0 ? (
            admin.map((person) => (
              <tr key={person._id}>
                {/* Your table row content */}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No admin data available</td>
            </tr>
          )}
        </tbody>
      </table>
    </>
  );
};

export default AdminDashboard;
