// AdminPanel.jsx

import React, { useState, useEffect } from 'react';
import { getAllUserApi, loginAdmin, getPendingBluebooksApi, approveBluebookApi } from '../apis/Api'; // Import your API functions
import './AdminPanel.css'; // Import your CSS file

const AdminPanel = () => {
  const [adminData, setAdminData] = useState({
    email: '',
    password: ''
  });
  const [token, setToken] = useState('');
  const [pendingBluebooks, setPendingBluebooks] = useState([]);

  useEffect(() => {
    // Check if admin is already logged in
    const adminToken = localStorage.getItem('adminToken');
    if (adminToken) {
      setToken(adminToken);
    }
  }, []);

  const handleAdminLogin = async () => {
    try {
      const response = await loginAdmin(adminData);
      localStorage.setItem('adminToken', response.data.token);
      setToken(response.data.token);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  const handleGetPendingBluebooks = async () => {
    try {
      const response = await getPendingBluebooksApi();
      setPendingBluebooks(response.data.bluebooks);
    } catch (error) {
      console.error('Error fetching pending bluebooks:', error);
    }
  };

  const handleApproveBluebook = async (bluebookId) => {
    try {
      await approveBluebookApi(bluebookId);
      // Remove the approved bluebook from the pending list
      setPendingBluebooks(prevBluebooks => prevBluebooks.filter(bluebook => bluebook._id !== bluebookId));
    } catch (error) {
      console.error('Error approving bluebook:', error);
    }
  };

  return (
    <div className="admin-panel">
      {!token ? (
        <div className="admin-login">
          <h2>Admin Login</h2>
          <input type="email" placeholder="Email" value={adminData.email} onChange={(e) => setAdminData({ ...adminData, email: e.target.value })} />
          <input type="password" placeholder="Password" value={adminData.password} onChange={(e) => setAdminData({ ...adminData, password: e.target.value })} />
          <button onClick={handleAdminLogin}>Login</button>
        </div>
      ) : (
        <>
          <h2>Welcome Admin!</h2>
          <button onClick={handleGetPendingBluebooks}>Get Pending Bluebooks</button>
          <div className="pending-bluebooks">
            <h3>Pending Bluebooks</h3>
            <ul>
              {pendingBluebooks.map(bluebook => (
                <li key={bluebook._id}>
                  <p>{bluebook.bluebookName}</p>
                  <p>{bluebook.vehicleNumber}</p>
                  <button onClick={() => handleApproveBluebook(bluebook._id)}>Approve</button>
                </li>
              ))}
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

export default AdminPanel;
