import React, { useState } from 'react';
import { loginAdmin } from '../apis/Api'; // Import your admin login API function
import { useNavigate } from 'react-router-dom';


const AdminLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    try {
      const response = await loginAdmin({ email, password }); // Call your admin login API function
      if (response.data.success) {
        localStorage.setItem('token', response.data.token); // Save token in local storage
        // Redirect to admin dashboard or any other page upon successful login
        history.push('/admin/dashboard');
      } else {
        alert(response.data.message); // Display error message if login fails
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Error logging in. Please try again.'); // Alert user if an error occurs
    }
  };

  return (
    <div>
      <h2>Admin Login</h2>
      <form onSubmit={(e) => { e.preventDefault(); handleLogin(); }}>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
