import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import { createLicenseApi, getAllLicenseApi } from "../../apis/Api";
import { useNavigate } from "react-router-dom";
import "./License.css"; // Import the CSS file

const License = () => {
  const [licenseName, setLicenseName] = useState("");
  const [licenseImage, setLicenseImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [licenses, setLicenses] = useState([]);
  const [isEditMode, setIsEditMode] = useState(true);
  const navigate = useNavigate();

  const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;


  useEffect(() => {
    fetchLicenses();
  }, []);

  const fetchLicenses = async () => {
    try {
      const response = await getAllLicenseApi();
      setLicenses(response.data.license || []);
    } catch (error) {
      console.error("Error fetching licenses:", error);
      toast.error("Error fetching licenses");
    }
  };

  const handleImage = (event) => {
    const file = event.target.files[0];
    setLicenseImage(file);
    setPreviewImage(URL.createObjectURL(file));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("licenseName", licenseName);
    formData.append("licenseImageUrl", licenseImage);
    formData.append("userId", loggedInUserId);
    createLicenseApi(formData)
      .then((res) => {
        if (res.data.success === false) {
          toast.error(res.data.message);
        } else {
          toast.success(res.data.message);
          setLicenseName(""); // Clear license name after successful submission
          setLicenseImage(null); // Clear license image after successful submission
          setPreviewImage(null); // Clear preview image after successful submission
          setIsEditMode(false); // Set edit mode to false after saving
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal Server Error");
      });
  };



  return (
    <>
      <div className="container">
        <div className="row justify-content-center mt-5">
          <div className="col-md-8">
            <div className="card">
              <div className="card-header">
                <h3 className="text-center">
                  {isEditMode ? "Create License" : "Saved Licenses"}
                </h3>
              </div>
              <div className="card-body">
                {isEditMode ? (
                  <form onSubmit={handleSubmit}>
                    <div className="form-group">
                      <label htmlFor="licenseName" className="license-number-label">License Number</label>
                      <input
                        id="licenseName"
                        type="text"
                        className="form-control license-number-box"
                        value={licenseName}
                        onChange={(e) => setLicenseName(e.target.value)}
                      />
                    </div>
                    <div className="form-group">
                      <label>Upload Image</label>
                      <input
                        type="file"
                        className="form-control-file"
                        onChange={handleImage}
                      />
                    </div>
                    {previewImage && (
                      <img
                        src={previewImage}
                        className="img-fluid mb-3 license-image"
                        alt="Preview"
                      />
                    )}
                    <button
                      type="submit"
                      className="btn btn-primary btn-block"
                    >
                      Save License
                    </button>
                  </form>
                ) : (
                  <div className="text-center">
                    {licenses.length > 0 ? (
                      <table className="table mt-4">
                        <thead className="table-dark">
                          <tr>
                            <th scope="col">License Name</th>
                            <th scope="col">License Image</th>
                          </tr>
                        </thead>
                        <tbody>
                          {licenses.map((item) => (
                            <tr key={item._id}>
                              <td>{item.licenseName}</td>
                              <td className="text-center">
                                <img
                                  src={item.licenseImageUrl}
                                  height={150}
                                  width={120}
                                  alt={item.licenseName}
                                />
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    ) : (
                      <p>No licenses saved yet.</p>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};


export default License;
