import React, { useState, useEffect } from "react";
import { followUserApi, searchUserApi } from "../../apis/Api";

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isFollowed, setIsFollowed] = useState(false);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = async () => {
    try {
      const response = await axios.post("/api/user/search", { phoneNo: searchQuery });
      setSearchResults(response.data.users);
    } catch (error) {
      console.error("Error searching for users:", error.message);
    }
  };

  const handleFollow = async (userId) => {
    try {
      const response = await axios.get(`/api/user/follow/${userId}`);
      if (response.data.success) {
        setIsFollowed(!isFollowed);
      }
    } catch (error) {
      console.error("Error following user:", error.message);
    }
  };

  return (
    <div>
      <h1>User Search Page</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Enter phone number"
      />
      <button onClick={handleSearchSubmit}>Search</button>

      <div>
        {searchResults.map((user) => (
          <div key={user._id}>
            <p>{user.firstName} {user.lastName}</p>
            {isFollowed ? (
              <button onClick={() => handleFollow(user._id)}>Unfollow</button>
            ) : (
              <button onClick={() => handleFollow(user._id)}>Follow</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchPage;
