import React, { useState } from "react";
import { searchUserApi } from "../" // Import your API function for searching users

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState(null); // State to store search result
  const [errorMessage, setErrorMessage] = useState(""); // State to store error message

  // Function to handle search query change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to handle search submission
  const handleSearchSubmit = () => {
    // Check if search query is empty
    if (!searchQuery.trim()) {
      setErrorMessage("Please enter a phone number.");
      return;
    }

    // Clear previous search result and error message
    setSearchResult(null);
    setErrorMessage("");

    // Send request to backend API to search for user by phone number
    searchUserApi({ phoneNo: searchQuery })
      .then((response) => {
        // Check if any users found
        if (response.data.success && response.data.users.length > 0) {
          setSearchResult(response.data.users);
        } else {
          setErrorMessage("No user registered with that phone number.");
        }
      })
      .catch((error) => {
        console.error("Error searching user:", error);
        setErrorMessage("An error occurred while searching. Please try again.");
      });
  };

  return (
    <div>
      <h1>Search Page</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Enter phone number to search"
      />
      <button onClick={handleSearchSubmit}>Search</button>
      {errorMessage && <p>{errorMessage}</p>}
      {searchResult && (
        <div>
          <h2>Search Result</h2>
          <ul>
            {searchResult.map((user) => (
              <li key={user._id}>
                {user.firstName} {user.lastName} - {user.email}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchPage;
