import React, { useState, useEffect } from 'react';
import { getAllLicenseApi } from '../../apis/Api';
import { Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './License.css';

const License = () => {
  const [licenses, setLicenses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchLicenses();
  }, []);

  const fetchLicenses = async () => {
    try {
      const response = await getAllLicenseApi();
      setLicenses(response.data.license);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching licenses:', error);
      toast.error('Error fetching licenses');
    }
  };

  return (
    <div className="license-container">
      <h2 className="license-title">License Entries</h2>
      {loading ? (
        <p>Loading...</p>
      ) : !licenses || licenses.length === 0 ? ( // Add a null check before accessing length
        <div className="no-licenses">
          <p>No license entries found.</p>
          <Link to="/license/create" className="create-license-link">Create License</Link>
        </div>
      ) : (
        <div>
          {licenses.map(license => (
            <div key={license._id} className="license-card">
              <h3 className="license-name">{license.licenseName}</h3>
              <img src={license.licenseImageUrl} alt="License" className="license-image" />
              <p className="license-info">Created By: {license.createdBy}</p>
              <p className="license-info">Created At: {new Date(license.createdAt).toLocaleDateString()}</p>
            </div>
          ))}
          <Link to="/license/create" className="create-license-link">Create License</Link>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default License;
