const Dashboard = () => {
  const [followedUsers, setFollowedUsers] = useState([]);
  const [followingUsers, setFollowingUsers] = useState([]);
  const [greeting, setGreeting] = useState('');
  const [userName, setUserName] = useState('');

  useEffect(() => {
    // Fetch user's name from local storage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUserName(storedUser.firstName); // Assuming the first name is stored
    }

    // Set the greeting
    const currentDate = new Date();
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    const formattedDate = currentDate.toLocaleDateString(undefined, options);
    setGreeting(`Today is ${formattedDate}`);

    // Fetch followed and following users
    const fetchFollowedUsers = async () => {
      try {
        const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
        const followedResponse = await getAllFollowedUsersApi(loggedInUserId);
        setFollowedUsers(followedResponse.data.followedUsers || []);
        const followingResponse = await getAllFollowingUsersApi(loggedInUserId);
        setFollowingUsers(followingResponse.data.followedUsers || []);
      } catch (error) {
        console.error("Error fetching followed or following users:", error.message);
      }
    };

    fetchFollowedUsers();
  }, []);

  return (
    <div className="dashboard-container">
      <div className="greeting-section">
        <div className="greeting">{greeting}</div>
        <div className="username">Hi, {userName}</div>
      </div>
      <div className="content">
        <div className="main-content">
          <div className="bluebook-section">
            <Bluebook />
          </div>
          <div className="license-section">
            <License />
          </div>
        </div>
        <div className="side-content">
          <div className="user-profile-section">
            <h3>Follow</h3>
            {followedUsers && followedUsers.length > 0 ? (
              followedUsers.map((user) => (
                <div key={user._id} className="search-result-item">
                  <div className="search-result-details">
                    <h5>{user.firstName}</h5>
                    <h5>{user.lastName}</h5>
                    <p>{user.email}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>No users followed</p>
            )}
          </div>
          <div className="user-profile-section">
            <h3>Following</h3>
            {followingUsers && followingUsers.length > 0 ? (
              followingUsers.map((user) => (
                <div key={user._id} className="search-result-item">
                  <div className="search-result-details">
                    <h5>{user.firstName}</h5>
                    <h5>{user.lastName}</h5>
                    <p>{user.email}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>No users followed</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
