import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Profile.css'; // Import the CSS file
import dummyPhoto from '../assets/images/user.png'; // Import the dummy photo
import { updateUserProfileApi } from '../apis/Api'; // Import the API function to update user profile

const Profile = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [formData, setFormData] = useState({
    firstName: user.firstName,
    lastName: user.lastName,
    email: user.email,
    phoneNo: user.phoneNo,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleUpdateProfile = async () => {
    try {
      const loggedInUserId = JSON.parse(localStorage.getItem('user'))._id;
      const response = await updateUserProfileApi(loggedInUserId,formData);
      const updatedUser = response.data.user;
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      console.log('Profile updated successfully:', updatedUser);
    } catch (error) {
      console.error('Error updating profile:', error);
      // Handle error
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-header">
        <Link to="/dashboards" className="back-button">&#8249; Back</Link>
        <h2 className="profile-title">Profile</h2>
      </div>
      <div className="profile-info">
        <div className="profile-photo">
          <img src={dummyPhoto} alt="Dummy Photo" className="profile-photo-image" />
        </div>
        <div className="profile-fields">
          <div className="profile-field">
            <label className="profile-label">First Name:</label>
            <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} />
          </div>
          <div className="profile-field">
            <label className="profile-label">Last Name:</label>
            <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} />
          </div>
          <div className="profile-field">
            <label className="profile-label">Email:</label>
            <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
          </div>
          <div className="profile-field">
            <label className="profile-label">Phone Number:</label>
            <input type="tel" name="phoneNo" value={formData.phoneNo} onChange={handleInputChange} />
          </div>
          <div className="profile-field">
            <label className="profile-label">Status:</label>
            <div className="profile-value">{user.is_verified ? 'Verified' : 'Not Verified'}</div>
          </div>
        </div>
      </div>

      <button className="profile-button" onClick={handleUpdateProfile}>
        Update Profile
      </button>
    </div>
  );
};

export default Profile;
