import React, { useState, useEffect } from 'react';
import { getAllFollowingUsersApi, followUserApi, shareBluebookApi } from '../api'; // Import API functions

function Notification() {
    const [followingUsers, setFollowingUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [bluebookId, setBluebookId] = useState('');
    const [message, setMessage] = useState('');

    // Function to fetch all users that the current user follows
    const fetchFollowingUsers = async () => {
        try {
            const response = await getAllFollowingUsersApi(); // Call API function
            setFollowingUsers(response.data);
        } catch (error) {
            console.error('Error fetching following users:', error);
        }
    };

    // Function to handle following a user
    const handleFollowUser = async (userId) => {
        try {
            await followUserApi(userId); // Call API function to follow the selected user
            setMessage('User followed successfully!');
        } catch (error) {
            console.error('Error following user:', error);
            setMessage('Error following user. Please try again.');
        }
    };

    // Function to handle sharing bluebook with selected user
    const handleShareBluebook = async () => {
        if (!selectedUser || !bluebookId) {
            setMessage('Please select a user and enter a bluebook ID.');
            return;
        }
        try {
            // Call API function to share bluebook with selected user
            await shareBluebookApi(bluebookId, selectedUser._id);
            setMessage('Bluebook shared successfully!');
        } catch (error) {
            console.error('Error sharing bluebook:', error);
            setMessage('Error sharing bluebook. Please try again.');
        }
    };

    useEffect(() => {
        fetchFollowingUsers(); // Fetch following users when component mounts
    }, []);

    return (
        <div>
            <h2>Share Bluebook</h2>
            {message && <p>{message}</p>}
            <div>
                <label htmlFor="bluebookId">Bluebook ID:</label>
                <input
                    type="text"
                    id="bluebookId"
                    value={bluebookId}
                    onChange={(e) => setBluebookId(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="selectedUser">Select User to Share With:</label>
                <select
                    id="selectedUser"
                    value={selectedUser ? selectedUser._id : ''}
                    onChange={(e) => {
                        const selectedUserId = e.target.value;
                        setSelectedUser(followingUsers.find(user => user._id === selectedUserId));
                    }}
                >
                    <option value="">Select User</option>
                    {followingUsers.map(user => (
                        <option key={user._id} value={user._id}>{user.firstName} {user.lastName}</option>
                    ))}
                </select>
                {!selectedUser && <button disabled>Select User</button>}
                {selectedUser && (
                    <button onClick={() => handleFollowUser(selectedUser._id)}>
                        Follow {selectedUser.firstName}
                    </button>
                )}
            </div>
            <button onClick={handleShareBluebook}>Share Bluebook</button>
        </div>
    );
}

export default Notification;
