import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Profile.css';
import dummyPhoto from '../assets/images/user.png';
import { updateUser } from '../apis/Api';
import { toast } from 'react-toastify';

const Profile = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')));
  const [formData, setFormData] = useState({
    firstName: user.firstName,
    lastName: user.lastName,
    email: user.email,
    phoneNo: user.phoneNo,
    avatar: user.avatar,
  });

  const handleSubmit = async () => {
    try {
      const response = await updateUser(user._id, formData);
      console.log('Profile updated successfully:', response.data);
      toast.success('Profile updated successfully');
      // Update local storage with the new user data
      localStorage.setItem('user', JSON.stringify(response.data.user));
      setUser(response.data.user);
    } catch (error) {
      console.error('Error updating profile:', error.message);
      toast.error('Error updating profile');
    }
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePhotoChange = async (e) => {
    try {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = () => {
        setFormData({
          ...formData,
          avatar: reader.result,
        });
      };
    } catch (error) {
      console.error('Error updating user profile:', error.message);
      toast.error('Error updating profile');
    }
  };

  useEffect(() => {
    setUser(JSON.parse(localStorage.getItem('user')));
  }, []);

  return (
    <div className="profile-container">
      <div className="profile-header">
        <Link to="/dashboards" className="back-button">&#8249; Back</Link>
        <h2 className="profile-title">Profile</h2>
      </div>
      <div className="profile-info">
        <div className="profile-photo">
          <label htmlFor="avatarInput">
            <img src={formData.avatar || user.avatar || dummyPhoto} alt="Profile" className="profile-photo-image" />
          </label>
          <input id="avatarInput" type="file" onChange={handlePhotoChange} accept="image/*" style={{ display: 'none' }} />
        </div>
        <div className="profile-fields">
          <div className="profile-field">
            <label className="profile-label">First Name:</label>
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Last Name:</label>
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Email:</label>
            <div className="profile-value">{formData.email}</div>
          </div>
          <div className="profile-field">
            <label className="profile-label">Phone Number:</label>
            <input
              type="tel"
              name="phoneNo"
              value={formData.phoneNo}
              onChange={handleInputChange}
            />
          </div>
          <div className="profile-field">
            <label className="profile-label">Status:</label>
            <div className="profile-value">{user.is_verified ? 'Verified' : 'Not Verified'}</div>
          </div>
        </div>
      </div>

      <button className="profile-button" onClick={handleSubmit}>
        Save Changes
      </button>
    </div>
  );
};

export default Profile;
