import React, { useState } from "react";
import { loginApi, forgetPasswordApi } from "../apis/Api";
import { toast } from "react-toastify";
import "./login.css";
import Slider from "../pages/Slider";
import { Link } from "react-router-dom";
import Icon2 from "../assets/images/loginpp.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [forgotPasswordEmail, setForgotPasswordEmail] = useState("");

  const changeEmail=(e)=>{
    setEmail(e.target.value);
  };
  const changePassword=(e)=>{
    setPassword(e.target.value);
  };

  const handleForgotPasswordEmail = (e) => {
    setForgotPasswordEmail(e.target.value);
}
  const handleSubmit = (e)=>{
    e.preventDefault();

    console.log(email, password);

    const data = {
        email: email,
        password: password,
    };
    loginApi(data).then((res)=>{
        if (res.data.success == true){
            toast.success(res.data.message);
            localStorage.setItem('token',res.data.token)

            const convertedJson = JSON.stringify(res.data.userData)
            localStorage.setItem("user",convertedJson)
        }else{
            toast.error(res.data.message);
        }
    })
    .catch((err)=> {
        console.log(err);
        toast.error("Internal Server Error of FrontEnd.")
    })
  };
  const forgotPassword = (e) => {
    e.preventDefault();
    const data = {
      email: forgotPasswordEmail,
    };
    forget(data)
      .then((res) => {
        if (res.data.success == true) {
          toast.success(res.data.message);
          // save token in local storage
          localStorage.setItem("token", res.data.token);
          // converting incomming json
          const convertedJson = JSON.stringify(res.data.user);
          localStorage.setItem("user", convertedJson);
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error("Internal server error");
      });
};
  return(
<div className="background-img d-flex justify-content-center">
      
      <div className="bodys d-lg-flex d-md-flex d-sm-block mt-4 ">
        <div className="left-container p-4">
          <div className="change-image">
            <Slider />
          </div>
        </div>
        <div className="right-container  ">
          <div className="d-flex justify-content-center mt-5"> <h2 className="pt-2 pb-2 text-center w-50 rounded-pill shadow-lg p-3 mb-5 bg-body rounded">Log In</h2></div> 
          <div className="d-flex justify-content-center">
          <img src={Icon2} height="150" width="140" /></div> 

              <input onChange={changeEmail}
                className="login-input form-control border-success mt-3"
                type="email"
                name="email"
                placeholder="Email"
                required=""
              />
              <div onChange= {changePassword}className='pass'>
                <input
                  className="login-input form-control border-success mt-3"
                  name="pswd"
                  placeholder="Password"
                  required
                />
              </div>
              <hr className="solid" />
              <button onClick={handleSubmit} className="login-btn bg-dark">Log In</button>
              <div className="mt-3 d-flex justify-content-end w-100">
                  <a
                    type="button"
                    className="text-decoration-none text-muted"
                    data-bs-toggle="modal"
                    data-bs-target="#staticBackdrop"
                  >
                    Forgot Password?
                  </a>

                  <div
                    class="modal fade"
                    id="staticBackdrop"
                    data-bs-backdrop="static"
                    data-bs-keyboard="false"
                    tabindex="-1"
                    aria-labelledby="staticBackdropLabel"
                    aria-hidden="true"
                  >
                    <div class="modal-dialog">
                      <div class="modal-content bg-dark text-white">
                        <div class="modal-header text-center align-items-center">
                          <h5
                            class="modal-title w-100"
                            id="staticBackdropLabel"
                          >
                            Change Password
                          </h5>
                          <button
                            type="button"
                            class="btn-close"
                            data-bs-dismiss="modal"
                            aria-label="Close"
                          ></button>
                        </div>
                        <div class="modal-body">
                          <label>Enter your email</label>
                          <input
                            type="email"
                            onChange={handleForgotPasswordEmail}
                            className="form-control text-light"
                            id="email"
                            placeholder="Enter your email"
                            style={{ color: "white" }}
                          />
                        </div>
                        <div class="modal-footer">
                          {/* <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button> */}
                          <button
                            type="button"
                            class="btn btn-secondary"
                            onClick={forgotPassword}
                          >
                            Update Password
                          </button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              <h3 className="pt-9">
                Don't have an account?{" "}
                <span className="signup">
                  {" "}
                  <Link to="/register">Sign In</Link>
                </span>
              </h3>
            </div>
          </div>
        </div>
      
  )
};

export default Login;
