import React, { useState, useNa } from 'react';
import { createBluebookApi } from '../../apis/Api';
import { useHistory } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const NextPage = ({ bluebookData }) => {
  const [formData, setFormData] = useState({
    chassisNumber: '',
    engineNumber: ''
  });

  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Combine bluebookData and formData
      const updatedBluebookData = { ...bluebookData, ...formData };
      await createBluebookApi(updatedBluebookData);
      toast.success('Bluebook entry created successfully');
      // Redirect to home page after successful creation
      history.push('/');
    } catch (error) {
      console.error('Error creating bluebook entry:', error.message);
      toast.error('Error creating bluebook entry. Please try again.');
    }
  };

  return (
    <div>
      <h2>Enter Chassis Number and Engine Number</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="chassisNumber">Chassis Number:</label>
          <input type="text" id="chassisNumber" name="chassisNumber" value={formData.chassisNumber} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="engineNumber">Engine Number:</label>
          <input type="text" id="engineNumber" name="engineNumber" value={formData.engineNumber} onChange={handleChange} required />
        </div>
        <ToastContainer position="bottom-right" autoClose={3000} hideProgressBar newestOnTop closeOnClick rtl={false} pauseOnFocusLoss draggable pauseOnHover />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default NextPage;
