import React from "react";
import "./Sidebar.css";
import { useNavigate } from "react-router-dom";

const Sidebar = ({ setSelectedItem, isSidebarOpen, handleToggleSidebar }) => {
  const liStyle = {
    backgroundColor: "white",
    padding: "0.5rem",
    marginLeft: "-20px",
    borderRadius: "10px",
  };

  const navigate = useNavigate();

  const handleItemClick = (item) => {
    setSelectedItem(item);
    handleToggleSidebar();
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login"); // Redirect to login page after logout
  };

  return (
    <div
      className={`sidebar ${isSidebarOpen ? "open" : ""}`}
      style={{ backgroundColor: "#E1D0C0" }}
    >
      {/* ... your existing code ... */}

      <button onClick={handleLogout} className="logoutBtn bottom-0 position-absolute" style={{ width: '80%' }}>
        {/* Your logout button content */}
      </button>
    </div>
  );
};

export default Sidebar;
