import React, { useState, useEffect } from "react";
import * as Api from "../apis/Api";
import { useHistory } from 'react-router-dom';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch all users when the component mounts
    fetchAllUsers();
  }, []);

  const fetchAllUsers = async () => {
    try {
      const response = await Api.viewAllUsersApi();
      setUsers(response.data.data);
    } catch (error) {
      console.error("Error fetching users:", error.message);
    } finally {
      setLoading(false);
    }
  };

  const viewUserProfile = async (userId) => {
    try {
      const response = await Api.viewUserProfileApi(userId);
      const userProfile = response.data.data; // Handle the user profile data as needed
      console.log("User Profile:", userProfile);
    } catch (error) {
      console.error("Error viewing user profile:", error.message);
    }
  };

  const deleteUser = async (userId) => {
    try {
      await Api.deleteUserApi(userId);
      // Optionally, you can update the users state to reflect the changes
      // For example, if you have a state variable like setSelectedUserId, you can reset it:
      // setSelectedUserId(null);
      // Then, refetch the updated user list
      fetchAllUsers();
      console.log("User deleted successfully");
    } catch (error) {
      console.error("Error deleting user:", error.message);
    }
  };

  const deleteUserLicense = async (licenseId) => {
    try {
      await Api.deleteUserLicenseApi(licenseId);
      console.log("License deleted successfully");
      // You may want to update the user's license list accordingly
    } catch (error) {
      console.error("Error deleting license:", error.message);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <ul>
        {users.map((user) => (
          <li key={user._id}>
            {user.firstName} {user.lastName}
            <button onClick={() => viewUserProfile(user._id)}>View Profile</button>
            <button onClick={() => deleteUser(user._id)}>Delete User</button>
            {/* Optionally, you can map through user licenses and provide delete options */}
            {/* {user.licenses.map((license) => (
              <div key={license._id}>
                <img src={license.licenseImageUrl} alt="License" />
                <button onClick={() => deleteUserLicense(license._id)}>Delete License</button>
              </div>
            ))} */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
