import React, { useState } from 'react';
import { registerApi } from '../apis/Api';
import { toast } from 'react-toastify';
import "./login.css";
import Img1 from "../assets/images/Rectangle5.png";
import Slider from "../pages/Slider";
import { Link } from 'react-router-dom';
import Password from './passsword_signup';
import Footer from "../components/footer";


const Register = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNo, setPhoneNo] = useState('');
  const [password, setPassword] = useState('');

  const changeFirstName = (e) => {
    setFirstName(e.target.value);
  };
  const changeLastName = (e) => {
    setLastName(e.target.value);
  };
  const changePhoneNo = (e) => {
    setPhoneNo(e.target.value);
  };
  const changePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(firstName, lastName, phoneNo, password);

    const data = {
      firstName: firstName,
      lastName: lastName,
      phoneNo: phoneNo,
      password: password,
    };
    registerApi(data)
      .then((res) => {
        if (res.data.success === true) {
          toast.success(res.data.message);
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error('Internal Server Error');
      });
  };

  return (
    <div>
   <div className="bodys d-lg-flex d-md-flex d-sm-block ">
   <div className="left-container p-5">
   <div className="change-image">
   <Slider/>
   </div>
   </div>
   <div className="right-container  ">
   
   <h2 className="pt-6 pb-2">
   Sign Up
   </h2>
   <div className="login">
           <div className="login-form">
                   <input className="login-input" type="name" name="name" placeholder="First Name" required=""/>
             <input className="login-input" type="email" name="email" placeholder="Last Name" required=""/>
                       <input className="login-input" type="number" name="phone_number" placeholder="Phone Number" required=""/>
                       <Password/>
             <hr className="solid"/>
             <button className="login-btn bg-dark">Sign Up</button>
                         <h3 className="pt-4">Already have account? <span className="signup"> <Link to="/">Sign in</Link></span></h3>
           
           </div>
         </div>
   </div>
   </div>
   <Footer/>
           </div>
  );
};

export default Register;
