import React, { useState } from "react";
import { loginApi, adminLoginApi } from "../apis/Api"; // Import both loginApi and adminLoginApi
import { toast } from "react-toastify";
import "./login.css";
import Slider from "../pages/Slider";
import { Link, useNavigate } from "react-router-dom";
import Icon2 from "../assets/images/loginpp.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const changeEmail = (e) => {
    setEmail(e.target.value);
  };

  const changePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {
      email: email,
      password: password,
    };

    try {
      const res = await loginApi(data); // Try regular login
      console.log("Login response:", res); // Check the response from the backend
      if (res.data.success === true) {
        handleLoginResponse(res.data);
      } else {
        // Try admin login if regular login fails
        const adminRes = await adminLoginApi(data);
        console.log("Admin login response:", adminRes);
        if (adminRes.data.success === true) {
          handleLoginResponse(adminRes.data);
        } else {
          toast.error(adminRes.data.message);
        }
      }
    } catch (err) {
      console.log(err);
      toast.error(err.response?.data?.message || "Error logging in.");
    }
  };

  const handleLoginResponse = (data) => {
    toast.success(data.message);
    localStorage.setItem("token", data.token);
    if (data.userData.is_admin === 1) {
      navigate("/admin/usertable"); // Redirect to admin dashboard
    } else {
      navigate("/dashboards"); // Redirect to regular user dashboard
    }
  };

  const handleForgetPassword = () => {
    navigate("/forget-password");
  };

  return (
    <div className="background-img d-flex justify-content-center">
      <div className="bodys d-lg-flex d-md-flex d-sm-block mt-4 ">
        <div className="left-container p-4">
          <div className="change-image">
            <Slider />
          </div>
        </div>
        <div className="right-container  ">
          <div className="d-flex justify-content-center mt-5">
            <h2 className="pt-2 pb-2 text-center w-50 rounded-pill shadow-lg p-3 mb-5 bg-body rounded">Log In</h2>
          </div>
          <div className="d-flex justify-content-center">
            <img src={Icon2} alt="icon img" height="150" width="140" />
          </div>

          <input
            onChange={changeEmail}
            className="login-input form-control border-success mt-3"
            type="email"
            name="email"
            placeholder="Email"
            required=""
          />
          <div onChange={changePassword} className='pass'>
            <input
              className="login-input form-control border-success mt-3"
              name="pswd"
              type="password"
              placeholder="Password"
              required
            />
          </div>
          <hr className="solid" />
          <button onClick={handleSubmit} className="login-btn bg-dark">Log In</button>
          <button onClick={handleForgetPassword} className="forget-password-btn">Forget Password?</button>

          <h3 className="pt-9">
            Don't have an account?{" "}
            <span className="signup">
              {" "}
              <Link to="/register">Create Account</Link>
            </span>
          </h3>
        </div>
      </div>
    </div>
  );
};

export default Login;
