// NotificationPage.jsx

import React, { useState, useEffect } from 'react';
import { getNotificationsApi, followBackApi } from '../../apis/Api';

const Notifications = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      const response = await getNotificationsApi();
      setNotifications(response.data.notifications);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const handleFollowBack = async (notification) => {
    try {
      await followBackApi(notification.senderId._id);
      // Refresh notifications after follow-back
      fetchNotifications();
    } catch (error) {
      console.error('Error following back:', error);
    }
  };

  const renderFollowBackButton = (notification) => {
    const isFollowing = notification.senderId.followers.includes(notification.receiverId);
    if (isFollowing) {
      return <button onClick={() => handleFollowBack(notification)}>Follow Back</button>;
    } else {
      return null; // Don't render follow-back button if user is already following
    }
  };

  const renderNotificationText = (notification) => {
    const isFollowing = notification.senderId.followers.includes(notification.receiverId);
    if (isFollowing) {
      return `${notification.senderId.firstName} ${notification.senderId.lastName} started following you. ${notification.timestamp}`;
    } else {
      return `You started following ${notification.senderId.firstName} ${notification.senderId.lastName}. ${notification.timestamp}`;
    }
  };

  return (
    <div>
      <h1>Notifications</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {notifications.map(notification => (
            <li key={notification._id}>
              {/* Display notification content here */}
              {renderNotificationText(notification)}
              {notification.type === 'follow' && renderFollowBackButton(notification)}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default NotificationPage;
