import React, { useState, useEffect } from "react";
import { followUserApi, searchUserApi } from "../../apis/Api";

const SearchPage = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResult, setSearchResult] = useState(null);
  const [followStatus, setFollowStatus] = useState(false);

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = () => {
    searchUserApi({ phoneNo: searchQuery })
      .then((res) => {
        setSearchResult(res.data.users[0]); // Assuming only one user is returned
        // Check if the user is already followed
        setFollowStatus(res.data.users[0]?.isFollowed || false);
      })
      .catch((error) => {
        console.error("Error searching user:", error);
        setSearchResult(null);
        setFollowStatus(false); // Reset follow status if search fails
      });
  };

  const handleFollow = () => {
    // Make API call to follow/unfollow user
    const userId = searchResult._id; // Assuming searchResult contains user data
    followUserApi(userId)
      .then((res) => {
        // Update follow status based on API response
        setFollowStatus(res.data.success);
        console.log("Followed user successfully");
      })
      .catch((error) => {
        console.error("Error following user:", error);
        // Handle error, e.g., show an error message
      });
  };

  return (
    <div>
      <h1>Search Page</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Enter search query"
      />
      <button onClick={handleSearchSubmit}>Search</button>

      {searchResult && (
        <div>
          <h2>User Details</h2>
          <p>Name: {searchResult.firstName} {searchResult.lastName}</p>
          <p>Phone Number: {searchResult.phoneNo}</p>

          {followStatus ? (
            <button onClick={handleFollow}>Following</button>
          ) : (
            <button onClick={handleFollow}>Follow</button>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchPage;
